<template>
  <div class="timeline_main">
    <div class="timeline_axis">
      <!-- 进度条 -->
      <div class="axis_item" v-for="(time, index) in dateTimes" :key="index">
        <!-- 进度条刻度 -->
        <div
          class="axis_item_tick"
          :class="{ 'axis_item_tick_active':index === highlightIndex }"
          @mouseenter="hoverIndex = index"
          @mouseleave="hoverIndex = -1"
          @click="tickClick(time, index)"
        ></div>
        <!-- 刻度值 -->
        <div class="axis_item_label" v-if="dateTimeIndexes.indexOf(index) >=0 ">{{ time }}</div>
        <!-- 当前播放的日期值 -->
        <div
          class="axis_item_tip"
          v-if="index === highlightIndex || index === hoverIndex"
        >{{ time }}</div>
      </div>
    </div>
  </div>
</template>
<script>
export default {
  data () {
    return {
      // 时间轴刻度
      dateTimes: [
        '03-04',
        '03-05',
        '03-06',
        '03-07',
        '03-08',
        '03-09',
        '03-10',
        '03-11',
        '03-12',
        '03-13'
      ],
      // 鼠标移入的时间位置
      hoverIndex: 0,
      // 日期列表
      dateTimeIndexes: [],
      // 日期间的间隔
      interval: 200
    }
  },
  computed: {
    // 高亮的刻度
    highlightIndex() {
      return (
        (this.activeIndex === -1 && this.dateTimes.length - 1) ||
        this.activeIndex
      )
    }
  },
  mounted() {
    // 初始化时间轴
    this.renderTimeline()
  },
  methods: {
    // 初始化时间轴
    renderTimeline() {
      // 时间轴宽度
      const timelineWidth = this.$el.offsetWidth - 40
      // 日期个数
      const dateTimesSize = this.dateTimes.length
      console.log(dateTimesSize)
      console.log(timelineWidth)
    },
    // 点击刻度
    tickClick(time, index) {
      console.log(time, index)
      // 点击的是正在播放的日期 则直接返回
      if (this.playing) {
        return
      }
      // 激活的index变为点击的index
      this.activeIndex = index
    },
  }
}
</script>
<style lang="scss" scoped>
.timeline_main {
  padding: 10px;
  box-sizing: border-box;
  .timeline_control {
    overflow: hidden;
    margin-bottom: 30px;

    i {
      cursor: pointer;
      display: inline-block;
      font-style: normal;
    }

    .menu_icon {
      font-size: 20px;
      width: 20px;
      height: 20px;
      background-size: cover;
      background-repeat: no-repeat;
    }

    .menu_play {
      color: #00fffa;
      float: left;
    }

    .menu_icon_disabled {
      cursor: no-drop;
      opacity: 0.5;
    }

    .menu_setting {
      overflow: hidden;
      float: left;
      margin-left: 20px;

      i {
        float: left;

        &.speed {
          padding: 0 5px;
        }
      }

      span {
        color: #cee7ff;
      }
    }

    .icon_left {
      // background-image: url('../assets/icon_left.png');
      background-image: url(data:image/png;base64,);
    }

    .icon_right {
      // background-image: url('../assets/icon_right.png');
      background-image: url(data:image/png;base64,);
    }

    .icon_play {
      // background-image: url('../assets/icon_play.png');
      background-image: url(data:image/png;base64,);
    }

    .icon_pause {
      // background-image: url('../assets/icon_pause.png');
      background-image: url(data:image/png;base64,);
    }

    .icon_up {
      // background-image: url('../assets/icon_up.png');
      background-image: url(data:image/png;base64,);
    }

    .icon_down {
      // background-image: url('../assets/icon_down.png');
      background-image: url(data:image/png;base64,);
    }
  }

  .timeline_axis {
    position: relative;
    display: flex;
    justify-content: space-around;
    padding: 8px 0;

    &::before {
      content: '';
      width: 100%;
      height: 10px;
      position: absolute;
      left: 0;
      bottom: 8px;
      display: inline-block;
      background: rgba(0, 0, 0, 0.5);
    }
  }

  .axis_item {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;

    .axis_item_tick {
      display: inline-block;
      width: 4px;
      height: 20px;
      background: rgba(0, 0, 0, 0.5);
      transition: background 0.3s;
      cursor: pointer;

      &:hover {
        background: #00fffa;
      }
    }

    .axis_item_tick_active {
      background: #00fffa;
    }

    .axis_item_label {
      position: absolute;
      bottom: -30px;
      white-space: nowrap;
    }

    .axis_item_tip {
      position: absolute;
      top: -25px;
      padding: 2px 6px;
      border-radius: 2px;
      background: rgba(0, 0, 0, 0.5);
      white-space: nowrap;
      color: #fff;
    }
  }
}
</style>